function counter(e,r,t){let n=0,o=e/steps;e>steps&&(n=e%steps);let u=setInterval((function(){t.innerHTML=Math.round(n),n+=o,n>e&&(clearInterval(u),n=e,n=formatNumber(n),t.innerHTML=n)}),r/steps*1e3)}function formatNumber(e){return e.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g,"$1,")}function unformatNumber(e){return e.toString().replace(/[\s.,%]/g,"")}window.onload=()=>{num=document.querySelectorAll(".card-numbers-counter"),steps=300,duration=2;new IntersectionObserver((e,r)=>{e.forEach(e=>{if(e.isIntersecting){const t=e.target;num.forEach(e=>{counter(unformatNumber(e.innerText),duration,e)}),r.unobserve(t)}})},{root:null,rootMargin:"0px",threshold:.01}).observe(document.querySelector("#coutntercontainer"))};